const jobs = $input.all(); // All job listings
const users = $("Get row(s) in sheet").all(); // All user preferences from sheet

const jobMatches = new Map(); // Key: jobId, Value: job + matched emails + matchTypes

for (const jobItem of jobs) {
  const job = jobItem.json;

  const jobLocation = (job.Location || "").toLowerCase();
  const jobDescription = (job.Description || "").toLowerCase();
  const jobTags = (job["Skills/Tags"] || "").toLowerCase();
  const jobId = job["Job ID"];

  for (const user of users) {
    const userEmail = (user.json.Email || "").toLowerCase();

    const userLocations = (user.json.Location || "").toLowerCase().split(",").map(s => s.trim());
    const userKeywords = (user.json.Keyword || "").toLowerCase().split(",").map(s => s.trim());

    const locationMatched = userLocations.some(loc => jobLocation.includes(loc));
    const keywordMatched = userKeywords.some(kw =>
      jobDescription.includes(kw) || jobTags.includes(kw)
    );

    // Proceed only if keyword matched
    if (keywordMatched) {
      // Check if this job is already stored
      if (!jobMatches.has(jobId)) {
        jobMatches.set(jobId, {
          ...job,
          matchedEmail: [], // array of emails
          matchType: {}     // email => matchType
        });
      }

      const matchType = locationMatched ? "Location+Keyword" : "Keyword Only";

      const jobEntry = jobMatches.get(jobId);
      if (!jobEntry.matchedEmail.includes(userEmail)) {
        jobEntry.matchedEmail.push(userEmail);
        jobEntry.matchType[userEmail] = matchType;
      }
    }
  }
}

// Prepare final output
const output = [];
for (const job of jobMatches.values()) {
  output.push({ json: job });
}

return output;

