function normalize(str) {
  return (str || "").toLowerCase().trim();
}

function splitWords(text) {
  return normalize(text).split(/\W+/).filter(Boolean);
}

// Basic Levenshtein distance
function levenshtein(a, b) {
  const an = a.length;
  const bn = b.length;
  if (an === 0) return bn;
  if (bn === 0) return an;

  const matrix = Array.from({ length: an + 1 }, () => Array(bn + 1).fill(0));
  for (let i = 0; i <= an; i++) matrix[i][0] = i;
  for (let j = 0; j <= bn; j++) matrix[0][j] = j;

  for (let i = 1; i <= an; i++) {
    for (let j = 1; j <= bn; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      matrix[i][j] = Math.min(
        matrix[i - 1][j] + 1,
        matrix[i][j - 1] + 1,
        matrix[i - 1][j - 1] + cost
      );
    }
  }

  return matrix[an][bn];
}

function fuzzyMatchWords(source, targets) {
  const threshold = 2;
  const sourceWords = splitWords(source);
  const targetWords = splitWords(targets);
  return sourceWords.some(sw =>
    targetWords.some(tw => levenshtein(sw, tw) <= threshold)
  );
}

const results = [];

for (const user of users) {
  const email = user.json.Email || "";
  const keywords = user.json.Keywords || "";
  const locationPref = user.json.Location || "";

  const matches = [];

  for (const item of jobs) {
    const job = item.json;
    const jobTitle = job["Job Title"] || "";
    const jobLocation = job["Location"] || "";

    const keywordMatched = fuzzyMatchWords(jobTitle, keywords);
    const locationMatched = fuzzyMatchWords(jobLocation, locationPref);

    if (keywordMatched || locationMatched) {
      let matchType = "Location Only";
      if (keywordMatched && locationMatched) matchType = "Keyword and Location";
      else if (keywordMatched) matchType = "Keyword Only";

      const jobURL = job["Job URL"] || job.URL || "URL not available";
      const description = (job.Description || "").slice(0, 300) || "No description available.";
      const jobFormatted = `- **Job Title**: ${jobTitle}
- **Company**: ${job.Company || ""}
- **Location**: ${jobLocation}
- **Salary**: ${job.Salary || "Not mentioned"}
- **Experience Required**: ${job["Experience Required"] || "Not specified"}
- **Skills**: ${job["Skills/Tags"] || "Not specified"}
- **Short Description**: ${description}
- **Job URL**: [View Job Posting](${jobURL})`;

      matches.push({
        email,
        jobFormatted,
        matchType
      });
    }
  }

  // If no matches, assign one random job
  if (matches.length === 0 && jobs.length > 0) {
    const randomJob = jobs[Math.floor(Math.random() * jobs.length)].json;
    const jobURL = randomJob["Job URL"] || randomJob.URL || "URL not available";
    const description = (randomJob.Description || "").slice(0, 300) || "No description available.";
    const jobFormatted = `- **Job Title**: ${randomJob["Job Title"] || ""}
- **Company**: ${randomJob.Company || ""}
- **Location**: ${randomJob.Location || ""}
- **Salary**: ${randomJob.Salary || "Not mentioned"}
- **Experience Required**: ${randomJob["Experience Required"] || "Not specified"}
- **Skills**: ${randomJob["Skills/Tags"] || "Not specified"}
- **Short Description**: ${description}
- **Job URL**: [View Job Posting](${jobURL})`;

    matches.push({
      email,
      jobFormatted,
      matchType: "Randomly Assigned (No Match)"
    });
  }

  // Push all matches
  for (const match of matches) {
    results.push({ json: match });
  }
}

return results;

// Get all summaries from previous step
const allSummaries = $input.all();

return allSummaries.map(item => {
  const fullOutput = item.json.output;

  // Extract email from the output
  const emailMatch = fullOutput.match(/[\w.-]+@[\w.-]+\.\w+/);
  const email = emailMatch ? emailMatch[0] : "unknown@example.com";

  // Remove email from summary if needed
  const cleanedSummary = fullOutput.replace(email, "").trim();

  return {
    json: {
      Email: email,
      Summary: cleanedSummary
    }
  };
});

