// Minimal Markdown to HTML converter
function markdownToHTML(markdown) {
  return markdown
    .replace(/^## (.*$)/gim, '<h2>$1</h2>')
    .replace(/^### (.*$)/gim, '<h3>$1</h3>')
    .replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/gim, '<em>$1</em>')
    .replace(/`([^`]+)`/g, '<code>$1</code>')
    .replace(/\n-{3,}\n/g, '<hr>')
    .replace(/\n/g, '<br>');
}

// Group summaries by unique email
