for (const item of items) {
  const email = item.json.email?.trim();  // NOTE: use `email` and `message` now
  const message = item.json.message?.trim();

  if (!email || !message) continue;

  if (!grouped[email]) {
    grouped[email] = [];
  }

  grouped[email].push(message);
}

// Convert each grouped Markdown message to HTML
const result = [];

for (const email in grouped) {
  const markdownMessages = grouped[email];

  // Combine and convert to HTML
  const combinedMarkdown = markdownMessages.join('\n\n<hr>\n\n');
  const htmlMessage = markdownToHTML(combinedMarkdown);

  result.push({
    json: {
      email,
      message: htmlMessage  // final HTML version
    }
  });
}

return result;

const emailToDelete = $node["Webhook1"].json.body.email?.trim().toLowerCase();
const rows = $input.all();

const matchedRows = rows.filter(row => {
  const rowEmail = (row.json.Email || "").trim().toLowerCase();
  return rowEmail === emailToDelete;
});

// Sort row numbers in descending order to avoid shifting issues
matchedRows.sort((a, b) => b.json.row_number - a.json.row_number);

return matchedRows.map(row => ({
  json: {
    row_number: row.json.row_number
  }
}));
